document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".delete-user-btn").forEach((function(e){e.addEventListener("click",(function(t){e.disabled||confirm("Are you sure you want to delete this user?")||t.preventDefault()}))})),document.querySelectorAll(".update-user-btn").forEach((function(e){e.addEventListener("click",(function(t){e.disabled||confirm("Are you sure you want to update this user?")||t.preventDefault()}))}))}));